```{.r .cell-code}
annotated_plot <- base_plot +
  ggtext::geom_textbox(
    data = annot_oldest,
    mapping = aes(
      x = age_years - 2.5,
      y = factor(hrs_indoors),
      label = glue::glue(
        "The oldest cat is <span style='color:{highlight_col}'>{animal_id}</span> who is {age_years} years old."
      )
    ),
    halign = 0.5, hjust = 0.5,
    size = 2.5,
    lineheight = 0.5,
    family = body_font,
    box.color = text_col,
    color = text_col,
    alpha = 0.5,
    maxheight = unit(4, "lines"),
